var SummernoteUtils={init:function(b,a){if(jQuery.type(a)==="string"){return this._getContainer(a).summernote({lang:"zh-CN",focus:true,minHeight:300,});
}else{return a.summernote({lang:"zh-CN",focus:true,height:300,});}},destroy:function(a){if(jQuery.type(a)==="string"){return this._getContainer(a).summernote("destroy");
}else{return a.summernote("destroy");}},getCode:function(a){if(jQuery.type(a)==="string"){return encodeURIComponent(this._getContainer(a).summernote("code"));
}else{return encodeURIComponent(a.summernote("code"));}},setCode:function(b,a){if(jQuery.type(a)==="string"){this._getContainer(a).summernote("code",b);
}else{a.summernote("code",b);}},_getContainer:function(a){var b=jQuery("#"+a);if(b.length==0){b=jQuery("[name='"+a+"']");if(b.length==0){b=jQuery("."+a);
if(b.length==0){throw new Error(a+"指定的div不存在");}}}return b;}};